import React, { useState } from 'react';
import styled from 'styled-components';
import { FaSearch } from 'react-icons/fa';

const SearchContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
  padding: 10px;
`;

const SearchInput = styled.input`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 100%;
`;

const SearchButton = styled.button`
  background-color: #f0f0f0;
  border: none;
  padding: 10px 15px;
  border-radius: 5px;
  cursor: pointer;
  margin-left: 10px;
`
const SearchResultsContainer = styled.div`
  position: absolute;
  width: 300px;
  background-color: white;
  border: 1px solid #ccc;
  box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
  z-index: 1;
`;

const SearchResultItem = styled.div`
  padding: 10px;
  cursor: pointer;
  &:hover {
    background-color: #f5f5f5;
  }
`;

function SearchBar() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const productData = [
    { id: 1, name: 'Everest Turmeric (Haldi) 200 Gram', image: 'https://via.placeholder.com/150x150', price: 50, type: 'product' },
    { id: 2, name: 'Patanjali Turmeric (Haldi) Powder 100 Gram', image: 'https://via.placeholder.com/150x150', price: 40, type: 'product' },
    { id: 3, name: 'Patanjali Sabji Masala 100 Gram', image: 'https://via.placeholder.com/150x150', price: 60, type: 'product' },
    { id: 4, name: 'Patanjali Cumin (Jeera) Powder', image: 'https://via.placeholder.com/150x150', price: 35, type: 'product' },
    { id: 5, name: 'Spices Or Masala', image: 'https://via.placeholder.com/80x60', type: 'category'},
    { id: 6, name: 'Bathing Soaps', image: 'https://via.placeholder.com/80x60', type: 'category'}
  ];

  const handleSearch = (event) => {
    setSearchTerm(event.target.value);

    if (searchTerm) {
      const results = productData.filter(item =>
        item.name.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setSearchResults(results);
    } else {
      setSearchResults([]);
    }
  };

  return (
    <SearchContainer>
      <SearchInput
        type="text"
        placeholder="Search products or categories..."
        value={searchTerm}
        onChange={handleSearch}
      />
      <button><FaSearch /></button>

      {searchResults.length > 0 && (
        <SearchResultsContainer>
          {searchResults.map(result => (
            <SearchResultItem key={result.id} onClick={() => { setSearchTerm(result.name); setSearchResults([]); }}>
              {result.type === 'product' ? result.name : result.name}
            </SearchResultItem>
          ))}
        </SearchResultsContainer>
      )}
    </SearchContainer>
  );
}

export default SearchBar;
