import React, { useState } from 'react';
import styled from 'styled-components';
import { FaSearch } from 'react-icons/fa';

const SearchContainer = styled.div`
  display: flex;
  position: fixed;
  align-items: center;
  margin-bottom: 20px;
  padding: 10px;
`;

const SearchInput = styled.input`
  padding: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
  width: 100%;
`;

const SearchButton = styled.button`
  background-color: #f0f0f0;
  border: none;
  padding: 10px 15px;
  border-radius: 5px;
  cursor: pointer;
  margin-left: 10px;
`;

// Mock product data (replace with your actual API call)
const products = [
  { id: 1, name: 'Everest Turmeric', category: 'Spices' },
  { id: 2, name: 'Patanjali Honey', category: 'Food & Beverages' },
  { id: 3, name: 'Dove Soap', category: 'Personal Care' },
  { id: 4, name: 'Surf Excel Detergent', category: 'Household Cleaning' },
];

function SearchBar({ onSearch }) {
  const [searchTerm, setSearchTerm] = useState('');

  const handleInputChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const results = products.filter((product) =>
      product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      product.category.toLowerCase().includes(searchTerm.toLowerCase())
    );
    onSearch(results); // Pass the filtered results to App.js
  };

  return (
    <SearchContainer>
      <SearchInput
        type="text"
        placeholder="Search for products..."
        value={searchTerm}
        onChange={handleInputChange}
      />
      <SearchButton onClick={handleSubmit}>
        <FaSearch />
      </SearchButton>
    </SearchContainer>
  );
}

export default SearchBar;
