import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';

const ProductGrid = styled.div`
  display: flex;
  flex-direction: row;
  gap: 20px;
  padding: 10px;
  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const ProductCard = styled.div`
  border: 1px solid #ccc;
  padding: 10px;
  text-align: center;
  border-radius: 5px;
`;

const ProductImage = styled.img`
  max-width: 400px;
  width: 100%;
  height: auto;
`;

const CartButton = styled.button`
  background-color: #4CAF50;
  border: none;
  color: white;
  padding: 8px 12px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 12px;
  cursor: pointer;
  border-radius: 4px;
`;

function ProductList({ category }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        let response;
        if (category) {
          response = await axios.get(`https://fakestoreapi.com/products/category/${category}`);
        } else {
          response = await axios.get('https://fakestoreapi.com/products'); // Fetch all products if no category is selected
        }
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [category]);

  const handleAddToCart = (productId) => {
    const productToAdd = products.find(p => p.id === productId);
    if (productToAdd) {
      setCartItems([...cartItems, productToAdd]); // Add to cart
    }
  };

  return (
    <ProductGrid>
      {products.map((product) => (
        <ProductCard key={product.id}>
          <ProductImage src={product.image} alt={product.title} style={{ width: '100px', height: '150px' }} />
          <h3>{product.title}</h3>
          <p>${product.price}</p>
          <CartButton onClick={() => handleAddToCart(product.id)}>Add to Cart</CartButton>
        </ProductCard>
      ))}
    </ProductGrid>
  );
}

export default ProductList;
