import React from 'react';
import styled from 'styled-components';

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
  overflow-y: auto; /* Enable vertical scrolling */
  height: 400px; /* Set a fixed height for the scrollable area */
  padding: 10px;
`;

const CategoryContainer = styled.div`
  display: flex;
  overflow-y: auto; /* Enable horizontal scrolling */
  margin-bottom: 20px;
  padding: 10px;
  width: 400px;
  gap: 20px;
`;

const ProductCard = styled.div`
  border: 1px solid #ccc;
  padding: 10px;
  text-align: center;
  border-radius: 5px;
  max-width: 400px;
`
const ProductImage = styled.img`
  max-width: 400px;
  width: 100%;
  height: auto;
`;

const productData = [
    { id: 1, name: 'Everest Turmeric (Haldi) 200 Gram', image: '/placeholder.png', price: 50, category: 'Spices Or Masala' },
    { id: 2, name: 'Patanjali Turmeric (Haldi) Powder 100 Gram', image: '/placeholder.png', price: 40, category: 'Spices Or Masala' },
    { id: 3, name: 'Patanjali Sabji Masala 100 Gram', image: '/placeholder.png', price: 60, category: 'Spices Or Masala' },
    { id: 4, name: 'Patanjali Cumin (Jeera) Powder', image: '/placeholder.png', price: 35, category: 'Spices Or Masala' },
    { id: 5, name: 'Dove Bathing Bar', image: '/placeholder.png', price: 70, category: 'Bathing Soaps'},
    { id: 6, name: 'Hamam Soap', image: '/placeholder.png', price: 30, category: 'Bathing Soaps' },
    { id: 7, name: 'Johnson\'s Baby Powder', image: '/placeholder.png', price: 80, category: 'Baby Care'},
    { id: 8, name: 'Pampers Diapers', image: '/placeholder.png', price: 120, category: 'Baby Care' },
    // Add more products here...
];

function ProductList({ category, searchKeyword }) {
    const filteredProducts = productData.filter(product => {
      const categoryMatch = !category || product.category === category;
      const searchMatch = !searchKeyword || product.name.toLowerCase().includes(searchKeyword.toLowerCase());
      return categoryMatch && searchMatch;
    });

  return (
    <CategoryContainer>
      {filteredProducts.map((product) => (
        <ProductCard key={product.id}>
          <ProductImage src={product.image} alt={product.name} />
          <div>{product.name}</div>
          <div>Price: ${product.price}</div>
        </ProductCard>
      ))}
    </CategoryContainer>
  );
}

export default ProductList;
