import React from 'react';
import styled from 'styled-components';

const CategoryContainer = styled.div`
  display: flex;
  overflow-x: auto; /* Enable horizontal scrolling */
  margin-bottom: 20px;
  padding: 10px;
`;

const CategoryCard = styled.div`
  background-color: #f0f0f0;
  padding: 10px;
  border-radius: 5px;
  margin-right: 10px;
  cursor: pointer;
  text-align: center;
  width: 100%;
  height: auto;
//   width: 120px; /* Fixed width for cards */
`;

const categories = [
    { id: 1, name: 'Spices Or Masala', image: '/placeholder.png' },
    { id: 2, name: 'Bathing Soaps', image: '/placeholder.png' },
    { id: 3, name: 'Baby Care', image: '/placeholder.png' },
    { id: 4, name: 'Hair Oil & Hair Cares', image: '/placeholder.png' },
    { id: 5, name: 'Toothpaste & Toothbrush', image: '/placeholder.png' },
    { id: 6, name: 'Rice Sarees', image: '/placeholder.png' },
    { id: 7, name: 'Shampoo & Conditioners', image: '/placeholder.png' },
    { id: 8, name: 'Biscuits & Cookies', image: '/placeholder.png' },
    { id: 9, name: 'Juices & Drinks', image: '/placeholder.png' },
    { id: 10, name: 'Snacks & Namkeen', image: '/placeholder.png' },
];

function CategoryList({ onCategorySelect }) {
  const handleCategoryClick = (category) => {
    onCategorySelect(category.name);
  };

  return (
    <CategoryContainer>
      {categories.map((category) => (
        <CategoryCard key={category.id} onClick={() => handleCategoryClick(category)}>
          <img src={category.image} alt={category.name} />
          <div>{category.name}</div>
        </CategoryCard>
      ))}
    </CategoryContainer>
  );
}

export default CategoryList;
