import React, { useState } from 'react';
import styled from 'styled-components';
import SearchBar from './components/SearchBar';
import CategoryList from './components/CategoryList';
import ProductList from './components/ProductList';
import Footer from './components/Footer';
import SearchResults from './components/SearchResults'; // Import the new component

const AppContainer = styled.div`
  font-family: sans-serif;
  // padding: 20px;
`;

const Header = styled.div`
  border: 1px solid #ccc;
`;

function App() {
  const [selectedCategory, setSelectedCategory] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [showSearchResults, setShowSearchResults] = useState(false); // State to control visibility

  return (
    <AppContainer>
      <SearchBar onSearch={(results) => {
        setSearchResults(results);
        setShowSearchResults(true); // Show results when search is performed
      }} />

      {showSearchResults && <SearchResults results={searchResults} onClose={() => setShowSearchResults(false)} />}

      {!showSearchResults && (
        <>
          <h2 style={{ marginTop: '10px', padding: '10px' }}>Top Categories</h2>
          <CategoryList onCategorySelect={setSelectedCategory} />
          <h2 style={{ marginTop: '10px', padding: '10px' }}>Top Products</h2>
          <ProductList category={selectedCategory} />
        </>
      )}

      <Footer />
    </AppContainer>
  );
}

export default App;
