import React from 'react';
import styled from 'styled-components';

const ProductGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 20px;
`;

const ProductCard = styled.div`
  border: 1px solid #ccc;
  padding: 10px;
  text-align: center;
  border-radius: 5px;
`;

const productData = [
    { id: 1, name: 'Everest Turmeric (Haldi) 200 Gram', image: '/placeholder.png', price: 50 },
    { id: 2, name: 'Patanjali Turmeric (Haldi) Powder 100 Gram', image: '/placeholder.png', price: 40 },
    { id: 3, name: 'Patanjali Sabji Masala 100 Gram', image: '/placeholder.png', price: 60 },
    { id: 4, name: 'Patanjali Cumin (Jeera) Powder', image: '/placeholder.png', price: 35 },
];

function ProductList({ category }) {
  const filteredProducts = productData.filter(product =>
    category ? product.name.toLowerCase().includes(category.toLowerCase()) : true
  );

  return (
    <ProductGrid>
      {filteredProducts.map((product) => (
        <ProductCard key={product.id}>
          <img src={product.image} alt={product.name} />
          <div>{product.name}</div>
          <div>Price: ${product.price}</div>
        </ProductCard>
      ))}
    </ProductGrid>
  );
}

export default ProductList;
